# Base64 of a 48x48 purple PNG
echo "iVBORw0KGgoAAAANSUhEUgAAADAAAAAwCAYAAABXAvmHAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAAAdgAAAHYBTnsmCAAAABl0RVh0U29mdHdhcmUAd3d3Lmlua3NjYXBlLm9yZ5vuPBoAAABXSURBVGiB7c0xAQAACAMgqX8FJzASgNB7eFFVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVVdQcxuwHbU7YqSgAAAABJRU5ErkJggg==" | base64 -d > icon48.png

# Base64 of a 128x128 purple PNG
echo "" | base64 -d > icon128.png

echo "Icons created successfully!"
